describe("the Basic example page",function(){var e=require("./page.js");beforeEach(function(){e.get()}),describe("rendering of the tree data",function(){it("should render a tree with 3 root nodes",function(){e.getRootNodes().count().then(function(e){expect(e).toEqual(3)})}),it("should show 2 subnodes for the first root node",function(){e.getNodeAtPosition(1).getSubnodes().count().then(function(e){expect(e).toEqual(2)})}),it("should show the correct text for the subnodes",function(){e.getNodeAtPosition(1,1).getText().then(function(e){expect(e).toEqual("node1.1")}),e.getNodeAtPosition(1,2).getText().then(function(e){expect(e).toEqual("node1.2")})})}),describe("the dragging and dropping of nodes",function(){it("should allow moving a node below another node",function(){var t=e.getNodeAtPosition(2,1);t.getText().then(function(e){expect(e).toEqual("node2.1")}),browser.wait(browser.executeScript("arguments[0].scrollIntoView();",e.getNodeAtPosition(2).getHandle().getWebElement())),browser.actions().dragAndDrop(t.getHandle(),{x:2,y:80}).perform(),e.getNodeAtPosition(2,1).getText().then(function(e){expect(e).toEqual("node2.2")})})})});